  req.startTime = Date.now();
  req.correlationId = req.header('x-correlation-id') || `agent-

${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  req.estimatedCost = 0;
  req.budgetPct = 0;

  next();
