        .get('/unknown/endpoint')
        .expect(404);
    });

    it('should handle malformed JSON', async () => {
      const response = await request(app)
        .post('/emails/process')
        .set('Content-Type', 'application/json')
        .send('invalid json')
        .expect(400);
    });
  });

  describe('Response Format Consistency', () => {
    it('should return consistent response format for all endpoints', async () => {
      const endpoints = [
        { method: 'get', path: '/health' },
        { method: 'get', path: '/cron/jobs' },
        { method: 'get', path: '/cron/stats' }
      ];

      for (const endpoint of endpoints) {
        const response = await request(app)[endpoint.method](endpoint.path);

        expect(response.body).toHaveProperty('success');
        expect(response.body).toHaveProperty('data');
        expect(response.body).toHaveProperty('timestamp');
        expect(typeof response.body.success).toBe('boolean');
      }
    });

    it('should return error format for failed requests', async () => {
      const response = await request(app)
        .post('/emails/process')
        .send({}) // Missing required fields
        .expect(400);

      expect(response.body).toHaveProperty('success', false);
      expect(response.body).toHaveProperty('error');
      expect(response.body).toHaveProperty('timestamp');
    });
  });

  describe('Performance', () => {
    it('should respond to health check within acceptable time', async () => {
      const startTime = Date.now();

      await request(app)
        .get('/health')
        .expect(200);

      const responseTime = Date.now() - startTime;
      expect(responseTime).toBeLessThan(1000); // Should respond within 1 second
    });

    it('should handle concurrent requests', async () => {
      const requests = Array.from({ length: 10 }, () =>
        request(app).get('/health')
      );

      const responses = await Promise.all(requests);

      responses.forEach(response => {
        expect(response.status).toBe(200);
        expect(response.body.success).toBe(true);
      });
    });
  });
});

import { describe, it, expect, beforeEach, afterEach, vi } from 'vitest';
import { JobQueue, Job } from '../queues/job-queue.js';

// Mock Redis
const mockRedis = {
  hset: vi.fn(),
  hgetall: vi.fn(),
  zadd: vi.fn(),
  zpopmax: vi.fn(),
  del: vi.fn(),
  keys: vi.fn()
};

vi.mock('ioredis', () => ({
  default: vi.fn().mockImplementation(() => mockRedis)
}));

vi.mock('../utils/logger.js', () => ({
  logger: {
    info: vi.fn(),
    error: vi.fn(),
    warn: vi.fn()
  }
}));

vi.mock('../utils/metrics.js', () => ({
  prometheusMetrics: {
    counter: vi.fn().mockReturnValue({
      inc: vi.fn()
    }),
    histogram: vi.fn().mockReturnValue({
      observe: vi.fn()
    }),
    gauge: vi.fn().mockReturnValue({
      inc: vi.fn(),
      dec: vi.fn(),
      set: vi.fn()
    })
  }
}));

describe('JobQueue', () => {
  let jobQueue: JobQueue;

  beforeEach(() => {
    jobQueue = new JobQueue();
    vi.clearAllMocks();
  });

  afterEach(() => {
    vi.clearAllMocks();
  });

  describe('enqueue', () => {
    it('should enqueue a job successfully', async () => {
      const jobData = {
        type: 'email_processing' as const,
        payload: { messageId: 'email_123' },
        organizationId: 'org_456',
        priority: 'high' as const,
        maxRetries: 3
      };

      mockRedis.hset.mockResolvedValue(1);
      mockRedis.zadd.mockResolvedValue(1);

      const jobId = await jobQueue.enqueue(jobData);

      expect(jobId).toMatch(/^job_\d+_[a-z0-9]+$/);
      expect(mockRedis.hset).toHaveBeenCalledWith(
        `job:${jobId}`,
        expect.objectContaining({
          id: jobId,
          type: 'email_processing',
          status: 'pending',
          priority: 'high',
          organizationId: 'org_456'
        })
      );
      expect(mockRedis.zadd).toHaveBeenCalledWith(
        'queue:email_processing',
        800, // high priority score
        jobId
      );
    });

    it('should handle enqueue errors', async () => {
      const jobData = {
        type: 'email_processing' as const,
        payload: { messageId: 'email_123' },
        organizationId: 'org_456',
        priority: 'high' as const,
        maxRetries: 3
      };

      mockRedis.hset.mockRejectedValue(new Error('Redis error'));

      await expect(jobQueue.enqueue(jobData)).rejects.toThrow('Redis error');
    });
  });

  describe('dequeue', () => {
    it('should dequeue a job successfully', async () => {
      const jobId = 'job_123';
      const jobData = {
        id: jobId,
        type: 'email_processing',
        payload: JSON.stringify({ messageId: 'email_123' }),
        organizationId: 'org_456',
        priority: 'high',
        status: 'pending',
        createdAt: '2024-01-15T10:00:00Z',
        retryCount: '0',
        maxRetries: '3'
      };

      mockRedis.zpopmax.mockResolvedValue([jobId]);
      mockRedis.hgetall.mockResolvedValue(jobData);
      mockRedis.hset.mockResolvedValue(1);

      const job = await jobQueue.dequeue('email_processing');

      expect(job).toMatchObject({
        id: jobId,
        type: 'email_processing',
        payload: { messageId: 'email_123' },
        organizationId: 'org_456',
        priority: 'high',
        status: 'processing'
      });
      expect(mockRedis.hset).toHaveBeenCalledWith(
        `job:${jobId}`,
        {
          status: 'processing',
          startedAt: expect.any(String)
        }
      );
    });

    it('should return null when no jobs available', async () => {
      mockRedis.zpopmax.mockResolvedValue([]);

      const job = await jobQueue.dequeue('email_processing');

      expect(job).toBeNull();
    });

    it('should return null when job data not found', async () => {
      const jobId = 'job_123';
      mockRedis.zpopmax.mockResolvedValue([jobId]);
      mockRedis.hgetall.mockResolvedValue({});

      const job = await jobQueue.dequeue('email_processing');

      expect(job).toBeNull();
    });
  });

  describe('completeJob', () => {
    it('should complete a job successfully', async () => {
      const jobId = 'job_123';
      const jobData = {
        id: jobId,
        type: 'email_processing',
        payload: JSON.stringify({ messageId: 'email_123' }),
        organizationId: 'org_456',
        priority: 'high',
        status: 'processing',
        createdAt: '2024-01-15T10:00:00Z',
        startedAt: '2024-01-15T10:01:00Z',
        retryCount: '0',
        maxRetries: '3'
      };

      mockRedis.hgetall.mockResolvedValue(jobData);
      mockRedis.hset.mockResolvedValue(1);

      await jobQueue.completeJob(jobId, { result: 'success' });

      expect(mockRedis.hset).toHaveBeenCalledWith(
        `job:${jobId}`,
        {
          status: 'completed',
          completedAt: expect.any(String),
          result: JSON.stringify({ result: 'success' })
        }
      );
    });

    it('should throw error when job not found', async () => {
      const jobId = 'job_123';
      mockRedis.hgetall.mockResolvedValue({});

      await expect(jobQueue.completeJob(jobId)).rejects.toThrow('Job job_123 not found');
    });
  });

  describe('failJob', () => {
    it('should retry job when retry count is below max', async () => {
      const jobId = 'job_123';
      const jobData = {
        id: jobId,
        type: 'email_processing',
        payload: JSON.stringify({ messageId: 'email_123' }),
        organizationId: 'org_456',
        priority: 'high',
        status: 'processing',
        createdAt: '2024-01-15T10:00:00Z',
        startedAt: '2024-01-15T10:01:00Z',
        retryCount: '1',
        maxRetries: '3'
      };

      mockRedis.hgetall.mockResolvedValue(jobData);
      mockRedis.hset.mockResolvedValue(1);
      mockRedis.zadd.mockResolvedValue(1);

      await jobQueue.failJob(jobId, 'Processing error');

      expect(mockRedis.hset).toHaveBeenCalledWith(
        `job:${jobId}`,
        {
          status: 'retrying',
          retryCount: 2,
          error: 'Processing error'
        }
      );
      expect(mockRedis.zadd).toHaveBeenCalledWith(
        'queue:email_processing',
        1800, // high priority + 1000 for retry
        jobId
      );
    });

    it('should mark job as failed when max retries exceeded', async () => {
      const jobId = 'job_123';
      const jobData = {
        id: jobId,
        type: 'email_processing',
        payload: JSON.stringify({ messageId: 'email_123' }),
        organizationId: 'org_456',
        priority: 'high',
        status: 'processing',
        createdAt: '2024-01-15T10:00:00Z',
        startedAt: '2024-01-15T10:01:00Z',
        retryCount: '3',
        maxRetries: '3'
      };

      mockRedis.hgetall.mockResolvedValue(jobData);
      mockRedis.hset.mockResolvedValue(1);

      await jobQueue.failJob(jobId, 'Final error');

      expect(mockRedis.hset).toHaveBeenCalledWith(
        `job:${jobId}`,
        {
          status: 'failed',
          completedAt: expect.any(String),
          error: 'Final error'
        }
      );
    });
  });

  describe('getJob', () => {
    it('should get job successfully', async () => {
      const jobId = 'job_123';
      const jobData = {
        id: jobId,
        type: 'email_processing',
        payload: JSON.stringify({ messageId: 'email_123' }),
        organizationId: 'org_456',
        priority: 'high',
        status: 'pending',
        createdAt: '2024-01-15T10:00:00Z',
        retryCount: '0',
        maxRetries: '3'
      };

      mockRedis.hgetall.mockResolvedValue(jobData);

      const job = await jobQueue.getJob(jobId);

      expect(job).toMatchObject({
        id: jobId,
        type: 'email_processing',
        payload: { messageId: 'email_123' },
        organizationId: 'org_456',
        priority: 'high',
        status: 'pending'
      });
    });

    it('should return null when job not found', async () => {
      const jobId = 'job_123';
      mockRedis.hgetall.mockResolvedValue({});

      const job = await jobQueue.getJob(jobId);

      expect(job).toBeNull();
    });
  });

  describe('getStats', () => {
    it('should return job statistics', async () => {
      const jobKeys = ['job:job_1', 'job:job_2', 'job:job_3'];
      const jobData1 = {
        id: 'job_1',
        type: 'email_processing',
        status: 'pending',
        priority: 'high'
      };
      const jobData2 = {
        id: 'job_2',
        type: 'email_processing',
        status: 'processing',
        priority: 'normal'
      };
      const jobData3 = {
        id: 'job_3',
        type: 'graph_sync',
        status: 'completed',
        priority: 'low'
      };

      mockRedis.keys.mockResolvedValue(jobKeys);
      mockRedis.hgetall
        .mockResolvedValueOnce(jobData1)
        .mockResolvedValueOnce(jobData2)
        .mockResolvedValueOnce(jobData3);

      const stats = await jobQueue.getStats();

      expect(stats).toMatchObject({
        total: 3,
        pending: 1,
        processing: 1,
        completed: 1,
        failed: 0,
        retrying: 0,
        byType: {
          'email_processing': 2,
          'graph_sync': 1
        },
        byPriority: {
          'high': 1,
          'normal': 1,
          'low': 1
        }
      });
    });
  });

  describe('clearCompletedJobs', () => {
    it('should clear old completed jobs', async () => {
      const jobKeys = ['job:job_1', 'job:job_2'];
      const oldJobData = {
        id: 'job_1',
        status: 'completed',
        completedAt: new Date(Date.now() - 25 * 60 * 60 * 1000).toISOString() // 25 hours ago
      };
      const recentJobData = {
        id: 'job_2',
        status: 'completed',
        completedAt: new Date(Date.now() - 12 * 60 * 60 * 1000).toISOString() // 12 hours ago
      };

      mockRedis.keys.mockResolvedValue(jobKeys);
      mockRedis.hgetall
        .mockResolvedValueOnce(oldJobData)
        .mockResolvedValueOnce(recentJobData);
      mockRedis.del.mockResolvedValue(1);

      const clearedCount = await jobQueue.clearCompletedJobs(24);

      expect(clearedCount).toBe(1);
      expect(mockRedis.del).toHaveBeenCalledWith('job:job_1');
      expect(mockRedis.del).not.toHaveBeenCalledWith('job:job_2');
    });
  });

  describe('priority scoring', () => {
    it('should assign correct priority scores', async () => {
      const testCases = [
        { priority: 'urgent' as const, expectedScore: 1000 },
        { priority: 'high' as const, expectedScore: 800 },
        { priority: 'normal' as const, expectedScore: 500 },
        { priority: 'low' as const, expectedScore: 200 }
      ];

      for (const testCase of testCases) {
        const jobData = {
          type: 'email_processing' as const,
          payload: { messageId: 'email_123' },
          organizationId: 'org_456',
          priority: testCase.priority,
          maxRetries: 3
        };

        mockRedis.hset.mockResolvedValue(1);
        mockRedis.zadd.mockResolvedValue(1);

        await jobQueue.enqueue(jobData);

        expect(mockRedis.zadd).toHaveBeenCalledWith(
          'queue:email_processing',
          testCase.expectedScore,
          expect.any(String)
        );
      }
    });
  });
});

import http from 'k6/http';
import { check, sleep } from 'k6';
import { Rate, Trend } from 'k6/metrics';

// Custom metrics
const errorRate = new Rate('errors');
const responseTime = new Trend('response_time');

export const options = {
  stages: [
    { duration: '2m', target: 10 }, // Ramp up to 10 users
    { duration: '5m', target: 10 }, // Stay at 10 users
    { duration: '2m', target: 20 }, // Ramp up to 20 users
    { duration: '5m', target: 20 }, // Stay at 20 users
    { duration: '2m', target: 0 },  // Ramp down to 0 users
  ],
  thresholds: {
    http_req_duration: ['p(95)<2000'], // 95% of requests must complete below 2s
    http_req_failed: ['rate<0.1'],     // Error rate must be below 10%
    errors: ['rate<0.1'],              // Custom error rate below 10%
  },
};

const BASE_URL = __ENV.BASE_URL || 'http://localhost:3001';

export default function () {
  // Test health endpoint
  const healthResponse = http.get(`${BASE_URL}/health`);
  check(healthResponse, {
    'health check status is 200': (r) => r.status === 200,
    'health check response time < 500ms': (r) => r.timings.duration < 500,
    'health check has success field': (r) => JSON.parse(r.body).success === true,
  });
  errorRate.add(healthResponse.status !== 200);
  responseTime.add(healthResponse.timings.duration);

  sleep(1);

  // Test metrics endpoint
  const metricsResponse = http.get(`${BASE_URL}/metrics`);
  check(metricsResponse, {
    'metrics status is 200': (r) => r.status === 200,
    'metrics response time < 1000ms': (r) => r.timings.duration < 1000,
    'metrics content type is text/plain': (r) => r.headers['Content-Type'].includes('text/plain'),
  });
  errorRate.add(metricsResponse.status !== 200);
  responseTime.add(metricsResponse.timings.duration);

  sleep(1);

  // Test cron jobs endpoint
  const cronJobsResponse = http.get(`${BASE_URL}/cron/jobs`);
  check(cronJobsResponse, {
    'cron jobs status is 200': (r) => r.status === 200,
    'cron jobs response time < 1000ms': (r) => r.timings.duration < 1000,
    'cron jobs has jobs array': (r) => {
      const body = JSON.parse(r.body);
      return body.success && Array.isArray(body.data.jobs);
    },
  });
  errorRate.add(cronJobsResponse.status !== 200);
  responseTime.add(cronJobsResponse.timings.duration);

  sleep(1);

  // Test cron stats endpoint
  const cronStatsResponse = http.get(`${BASE_URL}/cron/stats`);
  check(cronStatsResponse, {
    'cron stats status is 200': (r) => r.status === 200,
    'cron stats response time < 1000ms': (r) => r.timings.duration < 1000,
    'cron stats has stats object': (r) => {
      const body = JSON.parse(r.body);
      return body.success && typeof body.data.stats === 'object';
    },
  });
  errorRate.add(cronStatsResponse.status !== 200);
  responseTime.add(cronStatsResponse.timings.duration);

  sleep(1);

  // Test email processing endpoint
  const emailData = {
    messageId: `email_${__VU}_${__ITER}`,
    organizationId: `org_${__VU}`
  };

  const emailResponse = http.post(
    `${BASE_URL}/emails/process`,
    JSON.stringify(emailData),
    {
      headers: { 'Content-Type': 'application/json' },
    }
  );
  check(emailResponse, {
    'email processing status is 200': (r) => r.status === 200,
    'email processing response time < 2000ms': (r) => r.timings.duration < 2000,
    'email processing has result': (r) => {
      const body = JSON.parse(r.body);
      return body.success && body.data.result;
    },
  });
  errorRate.add(emailResponse.status !== 200);
  responseTime.add(emailResponse.timings.duration);

  sleep(2);

  // Test bulk email processing endpoint
  const bulkEmailData = {
    messageIds: [
      `bulk_email_${__VU}_${__ITER}_1`,
      `bulk_email_${__VU}_${__ITER}_2`,
      `bulk_email_${__VU}_${__ITER}_3`
    ],
    organizationId: `org_${__VU}`
  };

  const bulkEmailResponse = http.post(
    `${BASE_URL}/emails/process/bulk`,
    JSON.stringify(bulkEmailData),
    {
      headers: { 'Content-Type': 'application/json' },
    }
  );
  check(bulkEmailResponse, {
    'bulk email processing status is 200': (r) => r.status === 200,
    'bulk email processing response time < 3000ms': (r) => r.timings.duration < 3000,
    'bulk email processing has results array': (r) => {
      const body = JSON.parse(r.body);
      return body.success && Array.isArray(body.data.results);
    },
  });
  errorRate.add(bulkEmailResponse.status !== 200);
  responseTime.add(bulkEmailResponse.timings.duration);

  sleep(2);
}

export function handleSummary(data) {
  return {
    'load-test-results.json': JSON.stringify(data, null, 2),
    stdout: `
Load Test Results:
==================
Total Requests: ${data.metrics.http_reqs.values.count}
Failed Requests: ${data.metrics.http_req_failed.values.count}
Error Rate: ${(data.metrics.http_req_failed.values.rate * 100).toFixed(2)}%
Average Response Time: ${data.metrics.http_req_duration.values.avg.toFixed(2)}ms
95th Percentile: ${data.metrics.http_req_duration.values['p(95)'].toFixed(2)}ms
Max Response Time: ${data.metrics.http_req_duration.values.max.toFixed(2)}ms

Thresholds:
- 95th percentile < 2000ms: ${data.metrics.http_req_duration.values['p(95)'] < 2000 ? 'PASS' : 'FAIL'}
- Error rate < 10%: ${data.metrics.http_req_failed.values.rate < 0.1 ? 'PASS' : 'FAIL'}

Test Duration: ${data.state.testRunDurationMs / 1000}s
    `,
  };
}

import http from 'k6/http';
import { check, sleep } from 'k6';
import { Rate, Trend, Counter } from 'k6/metrics';

// Custom metrics
const errorRate = new Rate('errors');
const responseTime = new Trend('response_time');
const emailProcessed = new Counter('emails_processed');
const bulkEmailsProcessed = new Counter('bulk_emails_processed');

export const options = {
  stages: [
    { duration: '1m', target: 5 },   // Ramp up to 5 users
    { duration: '2m', target: 5 },   // Stay at 5 users
    { duration: '1m', target: 15 },  // Ramp up to 15 users
    { duration: '3m', target: 15 },  // Stay at 15 users
    { duration: '1m', target: 30 },  // Ramp up to 30 users
    { duration: '3m', target: 30 },  // Stay at 30 users
    { duration: '1m', target: 50 },  // Ramp up to 50 users
    { duration: '2m', target: 50 },  // Stay at 50 users
    { duration: '2m', target: 0 },   // Ramp down to 0 users
  ],
  thresholds: {
    http_req_duration: ['p(95)<5000'], // 95% of requests must complete below 5s
    http_req_failed: ['rate<0.2'],     // Error rate must be below 20%
    errors: ['rate<0.2'],              // Custom error rate below 20%
    emails_processed: ['count>100'],   // Process at least 100 emails
  },
};

const BASE_URL = __ENV.BASE_URL || 'http://localhost:3001';

export default function () {
  // Stress test health endpoint
  const healthResponse = http.get(`${BASE_URL}/health`);
  check(healthResponse, {
    'health check status is 200': (r) => r.status === 200,
    'health check response time < 1000ms': (r) => r.timings.duration < 1000,
  });
  errorRate.add(healthResponse.status !== 200);
  responseTime.add(healthResponse.timings.duration);

  sleep(0.5);

  // Stress test email processing with high concurrency
  const emailData = {
    messageId: `stress_email_${__VU}_${__ITER}_${Date.now()}`,
    organizationId: `stress_org_${__VU % 10}` // Limit to 10 orgs to simulate realistic load
  };

  const emailResponse = http.post(
    `${BASE_URL}/emails/process`,
    JSON.stringify(emailData),
    {
      headers: { 'Content-Type': 'application/json' },
    }
  );

  const emailSuccess = check(emailResponse, {
    'email processing status is 200': (r) => r.status === 200,
    'email processing response time < 3000ms': (r) => r.timings.duration < 3000,
  });

  if (emailSuccess) {
    emailProcessed.add(1);
  }

  errorRate.add(emailResponse.status !== 200);
  responseTime.add(emailResponse.timings.duration);

  sleep(0.3);

  // Stress test bulk email processing
  const bulkEmailData = {
    messageIds: Array.from({ length: 5 }, (_, i) => 
      `stress_bulk_${__VU}_${__ITER}_${i}_${Date.now()}`
    ),
    organizationId: `stress_org_${__VU % 10}`
  };

  const bulkEmailResponse = http.post(
    `${BASE_URL}/emails/process/bulk`,
    JSON.stringify(bulkEmailData),
    {
      headers: { 'Content-Type': 'application/json' },
    }
  );

  const bulkSuccess = check(bulkEmailResponse, {
    'bulk email processing status is 200': (r) => r.status === 200,
    'bulk email processing response time < 5000ms': (r) => r.timings.duration < 5000,
  });

  if (bulkSuccess) {
    bulkEmailsProcessed.add(bulkEmailData.messageIds.length);
  }

  errorRate.add(bulkEmailResponse.status !== 200);
  responseTime.add(bulkEmailResponse.timings.duration);

  sleep(0.2);

  // Stress test cron endpoints
  const cronJobsResponse = http.get(`${BASE_URL}/cron/jobs`);
  check(cronJobsResponse, {
    'cron jobs status is 200': (r) => r.status === 200,
    'cron jobs response time < 2000ms': (r) => r.timings.duration < 2000,
  });
  errorRate.add(cronJobsResponse.status !== 200);
  responseTime.add(cronJobsResponse.timings.duration);

  sleep(0.1);

  // Stress test cron stats
  const cronStatsResponse = http.get(`${BASE_URL}/cron/stats`);
  check(cronStatsResponse, {
    'cron stats status is 200': (r) => r.status === 200,
    'cron stats response time < 2000ms': (r) => r.timings.duration < 2000,
  });
  errorRate.add(cronStatsResponse.status !== 200);
  responseTime.add(cronStatsResponse.timings.duration);

  sleep(0.1);

  // Stress test metrics endpoint
  const metricsResponse = http.get(`${BASE_URL}/metrics`);
  check(metricsResponse, {
    'metrics status is 200': (r) => r.status === 200,
    'metrics response time < 2000ms': (r) => r.timings.duration < 2000,
  });
  errorRate.add(metricsResponse.status !== 200);
  responseTime.add(metricsResponse.timings.duration);

  sleep(0.1);
}

export function handleSummary(data) {
  const totalEmails = data.metrics.emails_processed ? data.metrics.emails_processed.values.count : 0;
  const totalBulkEmails = data.metrics.bulk_emails_processed ? data.metrics.bulk_emails_processed.values.count : 0;
  const totalEmailOperations = totalEmails + totalBulkEmails;

  return {
    'stress-test-results.json': JSON.stringify(data, null, 2),
    stdout: `
Stress Test Results:
====================
Test Duration: ${(data.state.testRunDurationMs / 1000).toFixed(2)}s
Max VUs: ${data.state.maxVUs}

Performance Metrics:
- Total Requests: ${data.metrics.http_reqs.values.count}
- Failed Requests: ${data.metrics.http_req_failed.values.count}
- Error Rate: ${(data.metrics.http_req_failed.values.rate * 100).toFixed(2)}%
- Average Response Time: ${data.metrics.http_req_duration.values.avg.toFixed(2)}ms
- 95th Percentile: ${data.metrics.http_req_duration.values['p(95)'].toFixed(2)}ms
- Max Response Time: ${data.metrics.http_req_duration.values.max.toFixed(2)}ms

Email Processing:
- Individual Emails Processed: ${totalEmails}
- Bulk Emails Processed: ${totalBulkEmails}
- Total Email Operations: ${totalEmailOperations}
- Emails per Second: ${(totalEmailOperations / (data.state.testRunDurationMs / 1000)).toFixed(2)}

Thresholds:
- 95th percentile < 5000ms: ${data.metrics.http_req_duration.values['p(95)'] < 5000 ? 'PASS' : 'FAIL'}
- Error rate < 20%: ${data.metrics.http_req_failed.values.rate < 0.2 ? 'PASS' : 'FAIL'}
- Emails processed > 100: ${totalEmailOperations > 100 ? 'PASS' : 'FAIL'}

System Health:
- Memory Usage: ${data.metrics.vus_max.values.max} max VUs
- Request Rate: ${(data.metrics.http_reqs.values.count / (data.state.testRunDurationMs / 1000)).toFixed(2)} req/s
    `,
  };
}

