// Middleware bsico
// CORS endurecido: slo orgenes explcitos y manejo de preflight
const allowedOrigins = Array.from(new Set([
  ...(process.env.ALLOWED_ORIGINS ? process.env.ALLOWED_ORIGINS.split(",").map(o => o.trim()).filter(Boolean) : []),
  process.env.WEB_URL,
  process.env.NEXT_PUBLIC_WEB_URL,
  "https://www.econeura.com",
].filter(Boolean)));

const corsOptions: CorsOptions = {
  origin: (origin, callback) => {
    if (!origin) return callback(null, true); // allow non-browser clients
    if (allowedOrigins.includes(origin)) return callback(null, true);
    return callback(null, false);
  },
  credentials: true,
  methods: ["GET", "POST", "PUT", "PATCH", "DELETE", "OPTIONS"],
  allowedHeaders: [
    "Content-Type",
    "Authorization",
    "X-Requested-With",
    "X-Correlation-Id",
    "X-Request-Id",
    "X-Trace-Id",
  ],
  optionsSuccessStatus: 204,
};

app.use(cors(corsOptions));
app.options("*", cors(corsOptions));
app.use(express.json());

// Middleware de Feature Flags (agregar informacin a todas las respuestas)
app.use(featureFlagInfoMiddleware());

// Middleware de observabilidad
app.use(observabilityMiddleware);

// Middleware de rate limiting (aplicar antes de las rutas)
