const FORWARD_HEADERS = [
  'x-user-id',
  'x-organization-id',
  'x-correlation-id',
  'authorization',
  'content-type'
];

// Funcin para hacer proxy de requests al API
async function proxyRequest(
  request: NextRequest,
  path: string,
  method: string = 'GET'
): Promise<NextResponse> {
  try {
    // Construir URL del API
    const apiUrl = `${API_BASE_URL}${CONFIG_API_PATH

}${path}`;

    // Headers a enviar
    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
      'User-Agent': 'ECONEURA-Web-BFF/1.0'
    };

    // Copiar headers relevantes
    FORWARD_HEADERS.forEach(headerName => {
      const headerValue = request.headers.get(headerName);
      if (headerValue) {
        headers[headerName] = headerValue;
      }
    });

    // Obtener body si existe
    let body: string | undefined;
    if (method !== 'GET' && method !== 'DELETE') {
      try {
        body = await request.text();
      } catch (error) {
        console.warn('Failed to read request body:', error);
      }
    }

    // Hacer request al API
    const response = await fetch(apiUrl, {
      method,
      headers,
      body,
      // Timeout de 30 segundos
      signal: AbortSignal.timeout(30000)
    });

    // Obtener respuesta
    const responseData = await response.text();

    // Headers de respuesta a copiar
    const responseHeaders: Record<string, string> = {
      'Content-Type': 'application/json',
      'X-BFF-Proxy': 'true',
      'X-API-Status'

,
        'X-Error': 'proxy-failed'
      }
    });
  }
}

